mapper.insert:
INSERT INTO TB_ATTACHMENT_FILE_INFO_JOIN (
    IDX_MEETING, FILE_PATH, FILE_NAME, FILE_SIZE
) VALUES (
    #{idx_meeting}, #{file_path}, #{file_name}, #{file_size}
)

mapper.select:
SELECT FILE_PATH, FILE_NAME
FROM TB_ATTACHMENT_FILE_INFO_JOIN 
WHERE DELETE_STAT=0 AND IDX_ATTACHMENT_FILE_INFO_JOIN=#{idx_attachment_file_info_join}

mapper.insert:
INSERT INTO TB_MEETING_MOVIE_FILE (
    IDX_MEETING, FILE_PATH, FILE_NAME, FILE_SIZE
) VALUES (
    (SELECT IDX_MEETING FROM TB_MEETING_USER_JOIN WHERE TOKEN=#{token} AND SESSIONID=#{mcid}), #{file_path}, #{file_name}, #{file_size}
)

INSERT INTO TB_ANALYSIS_FILE (
    IDX_ANALYSIS, FILE_PATH, FILE_NAME, FILE_SIZE
) VALUES (
    #{idx_analysis}, #{file_path}, #{file_name}, #{file_size}
)
