mapper.select:
SELECT A.*, B.USER_NAME FROM TB_MEETING A JOIN TB_USER B ON B.IDX_USER = A.IDX_USER WHERE A.DELETE_STAT=0 AND (A.IDX_USER=#{idx_user} OR A.IDX_MEETING IN (SELECT IDX_MEETING FROM TB_MEETING_USER_JOIN WHERE IDX_USER=#{idx_user} AND DELETE_STAT=0)) AND A.MT_END_DT > NOW() AND CASE WHEN A.IDX_USER!=#{idx_user} THEN A.MT_STATUS NOT IN (3,4) ELSE 1=1 END
AND LEFT(A.MT_START_DT,4)=#{calYear}
AND RIGHT(LEFT(A.MT_START_DT,7),2)=#{calMonth}
AND RIGHT(LEFT(A.MT_START_DT,10),2)=#{calDay}
ORDER BY
A.REG_DT DESC
FIELD(A.MT_STATUS, 0,1,2), A.MT_START_DT ASC
FIELD(A.MT_STATUS, 2,0,1), A.MT_START_DT ASC
A.MT_START_DT ASC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT A.*, IFNULL(B.USER_NAME,'') AS USER_NAME, IFNULL(D.FILE_NAME,'') AS FILE_NAME, IFNULL(D.FILE_PATH,'') AS FILE_PATH FROM TB_MEETING_USER_JOIN A LEFT OUTER JOIN TB_USER B ON B.IDX_USER=A.IDX_USER JOIN TB_MEETING C ON C.IDX_MEETING=A.IDX_MEETING AND C.IDX_USER!=A.IDX_USER LEFT OUTER JOIN TB_PROFILE_INFO D ON D.IDX_USER=A.IDX_USER WHERE A.DELETE_STAT=0
AND A.IDX_MEETING=#{idx_meeting}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO TB_MEETING (
    IDX_USER,
    MT_NAME,
    MT_START_DT,
    MT_END_DT,
    MT_INFO

    , MT_REMIND_TYPE


    , MT_REMIND_COUNT


    , MT_REMIND_WEEK


    , MT_REMIND_END

) VALUES (
    #{idx_user},
    #{mt_name},
    #{mt_start_dt},
    #{mt_end_dt},
    #{mt_info}

    , #{mt_remind_type}


    , #{mt_remind_count}


    , #{mt_remind_week}


    , #{mt_remind_end}

)

mapper.update:
UPDATE TB_MEETING 
    SET
        MT_NAME=#{mt_name},
        MT_START_DT=#{mt_start_dt},
        MT_END_DT=#{mt_end_dt},
        MT_INFO=#{mt_info}

        , MT_REMIND_TYPE=#{mt_remind_type}


        , MT_REMIND_COUNT=#{mt_remind_count}


        , MT_REMIND_WEEK=#{mt_remind_week}


        , MT_REMIND_END=#{mt_remind_end}

    WHERE IDX_MEETING=#{idx_meeting} AND IDX_USER=#{idx_user} AND DELETE_STAT=0

UPDATE TB_MEETING_USER_JOIN
    SET
        IS_LIVE=1, ZUID=#{zuid}
    WHERE TOKEN=#{token} AND SESSIONID=#{mcid}

UPDATE TB_MEETING_USER_JOIN
    SET
        IS_ALIVE=#{is_alive}
    WHERE TOKEN=#{token} AND SESSIONID=#{mcid} AND ZUID=#{zuid}
