mapper.select:
SELECT
    *
FROM (
    SELECT ROW_NUMBER() OVER(ORDER by #{order_field} DESC) AS row_num
        t.*
    FROM TB_USER t
    WHERE ROWNUM <= #{page_num}*#{amount}
)
WHERE row_num > (#{page_num} -1)*#{amount}

mapper.update:
UPDATE TB_USER
    SET LAST_UPD_DT = NOW()

        , USER_NAME = #{user_name}


        , USER_PWD = #{user_pwd}


        , USER_PHONE = #{user_phone}


        , EQ_TYPE01 = #{eq_type01}
        , EQ_TYPE02 = #{eq_type02}

    WHERE USER_ID = #{user_id}

UPDATE TB_USER
SET
    PROFILE_Y = #{profile_y}
WHERE IDX_USER=#{idx_user}

UPDATE TB_USER
SET
    AUTHKEY = #{authKey}
WHERE IDX_USER=#{idx_user}

UPDATE TB_USER
SET
    USER_STATUS = 1
WHERE USER_ID = #{user_id}
    AND AUTHKEY = #{authKey}

UPDATE TB_USER
SET
    TEMP_PW_Y = #{temp_pw_y},
    TEMP_PW = #{temp_pw},
    TEMP_PW_ISSUE_DT = #{temp_pw_issue_dt}
WHERE IDX_USER=#{idx_user}
